const serviceTextToObject = (service, config = false) => {
    let serviceObj = service
        .map((el) => el.replace(/\t/g, ''))
        .map((el) => el.trim())
        .flat();

    serviceObj[2] = 'DESC: ' + serviceObj[2];
    if (!config) {
        serviceObj[5] = 'STATES: ' + serviceObj[5];
    }

    serviceObj = serviceObj.map((item) =>
        item.split(/:(.*)/s).map((el) => el.trim()),
    );

    const result = {};
    serviceObj.forEach((item, index) => {
        result[item[0]] = item[1];
    });
    return result;
};

const parseServices = (services, config = false) => {
    let out = services.split('\r\n');
    out.splice(0, 5);
    let servicesArr = [];
    let sObj = [];
    out.forEach((element) => {
        if (element !== '') {
            sObj.push(element);
        } else {
            // sObj = serviceTextToObject(sObj);
            servicesArr.push(serviceTextToObject(sObj, config));
            sObj = [];
        }
    });
    servicesArr.splice(-1)
    return servicesArr;
};

